import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import '../../pages/Home/Slideer/Slideer.css'
import axios from 'axios'
import { NavLink} from 'react-router-dom'
import Sidebar from '../../components/SideBar/Sidebar/Sidebar'

const SliderDetail = () => {

    const [slider , setSlider] = useState([])

   const slider_arr = slider && slider.map(item => {
    return item._id
   })
   console.log(slider_arr)

    useEffect(() => {
      axios.get('http://localhost:5000/slider')
        .then(res => setSlider(res.data.sliders)) 
        .catch(error => console.log(error))
    } , [])

  return (
    <div className='row justify-content-center'>

         {slider.map((sliderss,_id) => (
         <div key={sliderss._id} id="carouselExampleControls" className="carousel slide homeslider" data-bs-ride="carousel">
         <div className="carousel-inner">
           <div className="carousel-item active">
             <img src={sliderss.image} className="d-block w-100 img" alt="adada"/>
             <div className="text1">
               <span className='span'>{sliderss.title}</span>
               <h2 className='h2'>{sliderss.description}</h2>
               <NavLink to={`/updateslider/${slider_arr[_id]}`} onClick={async() => {
                  await axios.put(`http://localhost:5000/slider/${slider_arr[_id]}`)
                  .then(res => res.data)
               }} className='me-1'>Update</NavLink>
               <NavLink to="/deleteslider"> Delete</NavLink>
             </div>
           </div>
         </div>
         <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
           <span className="carousel-control-prev-icon icon" aria-hidden="true"></span>
           <span className="visually-hidden">Previous</span>
         </button>
         <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
           <span className="carousel-control-next-icon icon" aria-hidden="true"></span>
           <span className="visually-hidden">Next</span>
         </button>
       </div>
       ))}
    </div>
  )
}

export default SliderDetail
