export const Sliders= createContext()


const SlidersContext = ({children}) => {
    const [slider,setSlider]=useState([])
  return (
    <Sliders.Provider value={{slider,setSlider}}>
        {children }
    </Sliders.Provider>
  )
}

export default SlidersContext
