import React, { useContext, useEffect } from 'react'
import { Slider } from '../../../Context/SlideersContext'
import './Slideer.css'



const url = 'http://localhost:3003/slider'

const Slideer = () => {

    const {slider,setSlider}=useContext(Slider)


    useEffect(()=>{
      fetch(url)
      .then(res=>res.json())
      .then(data=>{
        setSlider(data.slider)
        console.log(data.slider);
      })
    })

  return (
    <div>
        {
            slider.map(data=>{
                console.log(data);
                return(
                    <div id="carouselExampleControls" className="carousel slide homeslider" data-bs-ride="carousel">
                    <div className="carousel-inner">
                    <div className="carousel-item active">
                      <img src={data.image} className="d-block w-100 img" alt="..."/>
                      <div className="text1">
                            <span>Women Collection 2022</span>
                            <h2>NEW ARRIVALS</h2>
                            <a href="/">SHOP NOW</a>
                        </div>
                    </div>
                  </div>
                  <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
                    <span className="carousel-control-prev-icon icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Previous</span>
                  </button>
                  <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Next</span>
                  </button>
                </div>
                )
            })
        }

    </div>
  )
}

export default Slideer
