import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import '../../pages/Home/Slideer/Slideer.css'
import axios from 'axios'
import { NavLink} from 'react-router-dom'
import Sidebar from '../../components/SideBar/Sidebar/Sidebar'

const SliderDetail = () => {

    const [slider , setSlider] = useState([])

   const slider_arr = slider && slider.map(item => {
    return item._id
   })
   console.log(slider_arr)

    useEffect(() => {
      axios.get('http://localhost:5000/slider')
        .then(res => setSlider(res.data.sliders)) 
        .catch(error => console.log(error))
    } , [])

  return (
    <div className='row justify-content-between'>
      <table className='row col-lg-3'>
        {slider&&slider.map((sliderss,_id) => (
          <tbody key={sliderss._id}>
          <tr>
            <td><img className='img-fluid' src={sliderss.image} alt="" /></td>
            <td>{sliderss.title}</td>
            <td>{sliderss.description}</td>
          </tr>
        </tbody>
        ))}
      </table>
       <div className="slidersidebardetail col-lg-3">
       <Sidebar/>
       </div>
    </div>
  )
}

export default SliderDetail


