import React from 'react'
import { Box } from '@mui/system' 
import {Button,FormLabel,TextField} from "@mui/material"
import axios from 'axios'
import { useParams } from 'react-router-dom'
import { useState } from 'react'
import { useEffect } from 'react'
import './UpdateSlider.css'
import Sidebar from '../../components/SideBar/Sidebar/Sidebar'

const UpdateSlider = () => {


    const [inputs,setInputs] = useState({}) 

    const id = useParams().id
    console.log(id);


    useEffect(() => {
        const fetchHandler = async () => {
            await axios.get(`http://localhost:5000/slider/${id}`)
            .then(res=>res.data)
            .then(data=>setInputs(data.slider))
            // .then(res => res.data).then(data => setInputs(data.sliders))
        }
            fetchHandler();
    }, [id])

    const handleSubmit = (e) => {
        e.preventDefault();
        sendRequest();
    }

    const sendRequest = async () => {
        await axios.put(`http://localhost:5000/slider/${id}`,{
            title : String(inputs.title),
            description : String(inputs.description),
            image : String(inputs.image)
        }).then(res => res.data.slider)
    }

    const handleChange = (e) => {
        setInputs((prevState) => ({
            ...prevState,
            [e.target.name] : e.target.value
        }))
    }


  return (
    <div>
        {/* <form onSubmit={handleSubmit}>
            <Box
                display='flex'
                flexDirection = 'column'
                justifyContent='center'
                maxWidth={500}
                marginX='auto'
                marginTop={10}
                marginBottom={10}
            >
                <FormLabel>Title</FormLabel>
                <TextField value={inputs.title} onChange={handleChange} name='title' margin='normal'/>
                <FormLabel>Description</FormLabel>
                <TextField value={inputs.description} onChange={handleChange} name='description' margin='normal'/>
                <FormLabel>Image</FormLabel>
                <TextField value={inputs.image} onChange={handleChange} name='image' margin='normal'/>
                <Button  color='success' variant='contained' type='submit'>Update Slider</Button>
            </Box>
        </form> */}
         <div className="col-lg-3">
          <Sidebar/>
        </div>

        <div class="container updateslider col-lg-9">
      <form action="./index.html" method="POST" class="content">
        <h1 id="say-hello">Say Hello!</h1>
        <div>
          <div class="name">
            <label for="name">Your Name</label><br />
            <input class="fld" placeholder="Enter your name" id="name" />
          </div>

          <div class="email">
            <label for="email">Email</label><br />
            <input class="fld" placeholder="Enter your email address" id="email" />
          </div>

          <div class="services">
            <label for="services">Needed Services</label><br />
            <select class="slt" id="services">
              <option>Choose Services</option>
              <option>Unlimited API Requests</option>
            </select>
          </div>
        </div>

        <button class="btn" type="submit">Submit &#129046;</button>
      </form>
    </div>

    </div>
  )
}

export default UpdateSlider
