import React from 'react'
import { Box } from '@mui/system' 
import {Button,FormLabel,TextField} from "@mui/material"
import axios from 'axios'
import { useState } from 'react'
import Sidebar from '../../components/SideBar/Sidebar/Sidebar'

const AddSlider = () => {

    const [inputs,setInputs]=useState({
        title: '',
        description:'',
        image:''
    }) 
    const sendRequest=async()=>{
        await axios.post('http://localhost:5000/slider',{
            title: String(inputs.title),
            description: String(inputs.description),
            image: String(inputs.image)
        }).then(res=>res.data)
    }

    const handleChange=(e)=>{
        setInputs((prevState)=>({
            ...prevState,
            [e.target.name] : e.target.value
        }))
    }

    const handleSubmit=(e)=>{
        e.preventDefault();
        sendRequest();
    }

  return (
    <div>
        <Sidebar/>
    <form onSubmit={handleSubmit} action="">
        <Box
            display='flex'
            flexDirection='column'
            justifyContent='center'
            maxWidth={700}
            marginX='auto' 
            marginTop={5} 
            marginBottom={5}
        >
        <FormLabel>Title</FormLabel>
        <TextField value={inputs.title} onChange={handleChange} margin='normal' variant='outlined' name='title'/>   
        <FormLabel>Description</FormLabel>   
        <TextField value={inputs.description} onChange={handleChange} margin='normal' variant='outlined' name='description'/>    
        <FormLabel>Image</FormLabel>    
        <TextField value={inputs.image} onChange={handleChange} margin='normal' variant='outlined' name='image'/> 
        <Button color='success' variant='contained' type='submit'>ADD SLIDER</Button>    
        </Box>
    </form>
</div>
  )
}

export default AddSlider
