import axios from 'axios'
import React, { useEffect, useState } from 'react'

const Modal = ({image,title,hide}) => {
    const[product,setProducts]=useState([])

    useEffect(() => {
      axios.get('http://localhost:5000/card')
        .then(res => setProducts(res.data.cards)) 
        .catch(error => console.log(error))
    },[])
        let modalStyle={
            display:"block",
            backgroundColor:"rgba(140, 134, 134,0.1)",
        } 
  return (
    <div>
    {product&&product.map((productss)=>(
        <div className="modal show fade" style={modalStyle}>
            <div className="modal-dialog">
                <div className="modal-content">
                <div className="modal-header">
                    <h1 className="modal-title">{productss.title}</h1>
                    <button type="button" className="btn-close" onClick={hide}></button>
                </div>
                <div className="modal-body">
                    <p>{productss.price} AZN</p>
                    <img src={productss.image} className="img-fluid" alt="" />
                </div>
                </div>
            </div>  
        </div>
        ))}
    </div>
  )
}

export default Modal
