import React from 'react'
import { Box } from '@mui/system' 
import {Button,FormLabel,TextField} from "@mui/material"
import axios from 'axios'
import { useParams } from 'react-router-dom'
import { useState } from 'react'
import { useEffect } from 'react'

const UpdateSlider = () => { 
    const [inputs,setInputs]=useState({}) 
    const id=useParams().id;


useEffect(()=>{
    const fetchHandler= async()=>{
        await axios.get(`http://localhost:3001/slider/${id}`)
        .then(res=>res.data).then(data=>setInputs(data.sliders))
    }
    fetchHandler()
},[id])    


const handleChange=(e)=>{
    setInputs((prevState)=>({
        ...prevState,
        [e.target.name] : e.target.value
    }))
}

const sendRequest=async()=>{
    await axios.put(`http://localhost:3001/slider/${id}`,{
        title: String(inputs.title),
        description: String(inputs.description),
        image: String(inputs.image)
    }).then(res=>res.data)
}

const handleSubmit=(e)=>{
    e.preventDefault();
    sendRequest();
}

  return (
    <div>
        <form onSubmit={handleSubmit} action="">
            <Box
                display='flex'
                flexDirection='column'
                justifyContent='center'
                maxWidth={700}
                marginX='auto' 
                marginTop={5} 
                marginBottom={5}
            >
            <FormLabel>Title</FormLabel>
            <TextField value={inputs.title} onChange={handleChange} margin='normal' variant='outlined' name='title'/>    
            <FormLabel>Description</FormLabel>   
            <TextField value={inputs.description} onChange={handleChange} margin='normal' variant='outlined' name='description'/>    
            <FormLabel>Image</FormLabel>    
            <TextField value={inputs.image} onChange={handleChange} margin='normal' variant='outlined' name='image'/> 
            <Button color='warning' variant='contained' type='submit'>UPDATE SLIDER</Button>    
            </Box>
        </form>
    </div>
  )
}

export default UpdateSlider
